
-- FOR ALL AUTOINCREMENTS/GENERATED BY DEFAULT AS IDENTITY
-- If you insert directly into the table and do NOT use the default key
-- Then the next default value is incorrect and must be set again for the next default primary key
-- By doing this, we sync back the default key
SELECT SETVAL((SELECT PG_GET_SERIAL_SEQUENCE('"cart"', 'cart_id')), (SELECT (MAX("cart_id") + 1) FROM "cart"), FALSE);
SELECT SETVAL((SELECT PG_GET_SERIAL_SEQUENCE('"users"', 'user_id')), (SELECT (MAX("user_id") + 1) FROM "users"), FALSE);
SELECT SETVAL((SELECT PG_GET_SERIAL_SEQUENCE('"orders"', 'order_id')), (SELECT (MAX("order_id") + 1) FROM "orders"), FALSE);
SELECT SETVAL((SELECT PG_GET_SERIAL_SEQUENCE('"shipping"', 'shipping_id')), (SELECT (MAX("shipping_id") + 1) FROM "shipping"), FALSE);
